package java_fun;
import java.util.*;
public class question6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc=new Scanner(System.in);
		int a=sc.nextInt();
		if(a % 2 == 0) {
		System.out.println(a + " Even ");
		}
		else {
			System.out.println(a +" Odd ");
		}
	}

}
